(5, 'Josu Hernaiz', 31963, 'W', 2, '', 0, '{1}{W}', 2, '7db33454-8d71-5364-8d5a-a467b28faa32', 466759, 'Angelic Gift', 0, 0.02, 'common', 'Enchant creature\r\nWhen Angelic Gift enters the battlefield, draw a card.\r\nEnchanted creature has flying.', 227, 'Aura', 0, 'Enchantment'),
(6, 'Eric Deschamps', 31649, 'W', 1, '\"You cannot defend others if your own well-being is neglected.\"', 0, '{W}', 1, '31d8b53d-ac03-58f5-a415-1b35600abfb1', 447142, 'Ajani\'s Welcome', 0, 1.51, 'uncommon', 'Whenever a creature enters the battlefield under your control, you gain 1 life.', 226, '', 0, 'Enchantment'),
(6, 'Bastien L. Deharme', 31964, 'W', 2, 'Waves of gloom crash in vain against his unyielding light.', 0, '{1}{W}', 2, '58f6e1ae-1331-5a19-b977-56f1e10082ea', 466760, 'Apostle of Purifying Light', 2, 0.09, 'uncommon', 'Protection from black (This creature can\'t be blocked, targeted, dealt damage, enchanted, or equipped by anything black.)\r\n{2}: Exile target card from a graveyard.', 227, 'Human,Cleric', 1, 'Creature'),
(7, 'Livia Prima', 31650, 'W', 5, 'She sings for all those who have been silenced.', 0, '{4}{W}', 5, '753fb4f6-9274-594a-b695-64caf6a3bf40', 447143, 'Angel of the Dawn', 3, 0.02, 'common', 'Flying\r\nWhen Angel of the Dawn enters the battlefield, creatures you control get +1/+1 and gain vigilance until end of turn.', 226, 'Angel', 3, 'Creature'),
(7, 'Cristi Balanescu', 31965, 'W', 3, '\"With me! Break this line and the rest will fall!\"', 0, '{2}{W}', 3, '6d31b7d3-e09e-5e2c-a160-d68146afd9cc', 466761, 'Battalion Foot Soldier', 2, 0.02, 'common', 'When Battalion Foot Soldier enters the battlefield, you may search your library for any number of cards named Battalion Foot Soldier, reveal them, put them into your hand, then shuffle.', 227, 'Human,Soldier', 2, 'Creature'),
